$(document).ready(function(){$(".video").fitVids()});var jPanelMenu={};$(function(){$("pre").each(function(e,t){hljs.highlightBlock(t)});jPanelMenu=$.jPanelMenu({menu:"header .credits",animated:!0,openPosition:"300px"});jPanelMenu.on();$(document).on("click",jPanelMenu.menu+" li a",function(e){jPanelMenu.isOpen()&&$(e.target).attr("href").substring(0,1)=="#"&&jPanelMenu.close()});$(document).on("click","#trigger-off",function(e){jPanelMenu.off();$("html").css("padding-top","40px");$("#trigger-on").remove();$("body").append('<a href="" title="Re-Enable jPanelMenu" id="trigger-on">Re-Enable jPanelMenu</a>');e.preventDefault()});$(document).on("click","#trigger-on",function(e){jPanelMenu.on();$("html").css("padding-top",0);$("#trigger-on").remove();e.preventDefault()})});